import { useState } from 'react'
import { useWorkoutsContext } from '../hooks/useWorkoutsContext'
import { useAuthContext } from '../hooks/useAuthContext'

// date fns
import formatDistanceToNow from 'date-fns/formatDistanceToNow'

const WorkoutDetails = ({ workout }) => {
  const { dispatch } = useWorkoutsContext()
  const { user } = useAuthContext()
  const [isEditing, setIsEditing] = useState(false)
  const [editedWorkout, setEditedWorkout] = useState({
    title: workout.title,
    load: workout.load,
    reps: workout.reps
  })

  const handleDelete = async () => {
    if (!user) return

    const response = await fetch('/api/workouts/' + workout._id, {
      method: 'DELETE',
      headers: { 'Authorization': `Bearer ${user.token}` }
    })
    const json = await response.json()

    if (response.ok) {
      dispatch({ type: 'DELETE_WORKOUT', payload: json })
    }
  }

  const handleEdit = async () => {
    if (!user) return

    const response = await fetch('/api/workouts/' + workout._id, {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${user.token}`
      },
      body: JSON.stringify(editedWorkout)
    })

    const json = await response.json()

    if (response.ok) {
      dispatch({ type: 'UPDATE_WORKOUT', payload: json })
      setIsEditing(false)
    }
  }

  return (
    <div className="workout-details">
      {isEditing ? (
        // Edit Form
        <div className="edit-form">
          <input
            type="text"
            value={editedWorkout.title}
            onChange={(e) => setEditedWorkout({ ...editedWorkout, title: e.target.value })}
          />
          <input
            type="number"
            value={editedWorkout.load}
            onChange={(e) => setEditedWorkout({ ...editedWorkout, load: e.target.value })}
          />
          <input
            type="number"
            value={editedWorkout.reps}
            onChange={(e) => setEditedWorkout({ ...editedWorkout, reps: e.target.value })}
          />
          <button onClick={handleEdit}>Save</button>
          <button onClick={() => setIsEditing(false)}>Cancel</button>
        </div>
      ) : (
        // Workout Display
        <>
          <h4>{workout.title}</h4>
          <p><strong>Load (kg): </strong>{workout.load}</p>
          <p><strong>Reps: </strong>{workout.reps}</p>
          <p>{formatDistanceToNow(new Date(workout.createdAt), { addSuffix: true })}</p>
          <span className="material-symbols-outlined" onClick={handleDelete}>delete</span>
          <span className="material-symbols-outlined" onClick={() => setIsEditing(true)}>edit</span>
        </>
      )}
    </div>
  )
}

export default WorkoutDetails
